<script>
    import Vue from 'vue'
    import common from '../../common.js'
    import config from '../../../config/config.js'
    import modal from '../modal'

    export default {
        name: 'AuthorUpdate',
        data() {
            let id = this.$route.params.id;
            let router = this.$router;
			let http = this.$http;
            return new Vue({
                el: '#authorUpdate',
                data: {
                    author: {},
                    modalData: {},
                    showModal: false,
                    status: {
                        status: null,
                        message: null,
                        display: "none",
                        bootstrapPanelColor: null,
                    },
                    form: {
                        display: "block"
                    }
                },
                created: function () {
                    this.fetchData();
                },
                methods: {
                    saveAuthor: function (event) {
                        let author = {
                            'id': this.author.id,
                    		'firstName': this.author.firstName,
                    		'lastName': this.author.lastName,
                    		
                        };
						http.put(config.urlApi+'/author', author).then(response => {
                            common.apiConnect(this, response);
                            this.fetchData();
                        }, response => {
                            common.apiUnreachable(this);
                        });
                        this.status.display = "block";
                    },
                    deleteAuthor: function (modalData) {
						{
                            http.delete(config.urlApi+'/author/'+modalData.id).then(response => {
                                common.apiConnect(this, response);
                                if (response.status === 204) {
                                    this.form.display = "none";
                                    setTimeout(function () {
                                        router.push('/author');
                                    }, 3000);
                                }
                            }, response => {
                                common.apiUnreachable(this);
                            });
                            this.status.display = "block";
                        }
                    },
                    editAuthor (author) {
                        this.modalData = author;
                    },
                    fetchData: function () {
                        {
                            http.get(config.urlApi+'/author/'+id).then(response => {
                                if(response.body) {
                                    this.author = response.body;
                                }
                            }, response => {
                            common.apiUnreachable(this, response);
                                this.status.display = "block";
                            });
                        }
                    }
                }
            });
        }
    }
</script>